SET @agent1_id = (SELECT id FROM agents WHERE telegramId = 'demo_agent_1');
SET @agent2_id = (SELECT id FROM agents WHERE telegramId = 'demo_agent_2');
SET @agent3_id = (SELECT id FROM agents WHERE telegramId = 'demo_agent_3');

INSERT INTO payments (agentId, amount, status, method, requestedAt, completedAt, createdAt, updatedAt)
VALUES
